header = '''HTTP/1.1 200 OK\ncontent-length: 7061\n\n'''

body = '''<?xml version="1.0"?><methodResponse><params><param><value><struct><member><name>Status</name><value>Success</value></member><member><name>Value</name><value><struct><member><name>EFI-variables</name><value></value></member></struct></value></member></struct></value></param></params></methodResponse>'''

def breakup(text):
    l = []
    s = ''
    for i, c in enumerate(text):
        if i % 32 == 0 and i != 0:
            l.append('\t"' + s + '"\t\t\\\n')
            s = ''
        s += c

    l.append('\t"' + s + '"\t\t\\\n')
    return l

l = []

print("bodylen: ", len(body))

for piece in body.split('\n'):
    for chunk in breakup(piece):
        l.append(chunk)

string = ''.join(l)
print('"' + header + '"\n' + string)
